import React, { createContext, useState, useEffect } from 'react';
import initializeWalletKit from '../components/WalletConnection';

const WalletContext = createContext();

export const WalletContextProvider = ({ children }) => {
    const [walletKit, setWalletKit] = useState(null);
    const [accounts, setAccounts] = useState(); // Initialize as an empty array

    useEffect(() => {
        const setupWalletKit = async () => {
            const wk = await initializeWalletKit();
            setWalletKit(wk);
        };
        setupWalletKit();
    },);

    return (
        <WalletContext.Provider value={{ walletKit, accounts }}>
            {children}
        </WalletContext.Provider>
    );
};

export default WalletContext;
