import React, { createContext, useState, useEffect } from 'react';
import { Core } from '@walletconnect/core';
import { WalletKit } from '@reown/walletkit';

const WalletConnectContext = createContext();

export const WalletConnectProvider = ({ children }) => {
    const [walletKit, setWalletKit] = useState(null);
    const [accounts, setAccounts] = useState(); // Initialize accounts as an empty array

    useEffect(() => {
        const initializeWalletKit = async () => {
            // ... your existing initialization code ...

            try {
                const kit = await WalletKit.init({
                    // ...
                });
                setWalletKit(kit);

                // Fetch initial accounts after initializing walletKit
                const fetchedAccounts = await kit.getAccounts();
                setAccounts(fetchedAccounts);
            } catch (error) {
                // ...
            }
        };

        initializeWalletKit();
    },);

    // Provide both walletKit and accounts in the context value
    return (
        <WalletConnectContext.Provider value={{ walletKit, accounts }}>
            {children}
        </WalletConnectContext.Provider>
    );
};

export default WalletConnectContext;
