import React, { useContext } from 'react';
import WalletConnectContext from '../context/WalletConnectContext'; // Updated import
import axios from 'axios'; // Import axios

function ReputationScore() {
    const { accounts } = useContext(WalletConnectContext); // Updated context
    const [score, setScore] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchScore = async () => {
            if (accounts && accounts.length > 0) {
                setLoading(true);
                setError(null);

                try {
                    const response = await axios.get(`http://localhost:3001/users/${accounts[0]}/reputation`);
                    setScore(response.data.score);
                } catch (e) {
                    setError(e.message);
                } finally {
                    setLoading(false);
                }
            }
        };

        fetchScore();
    }, [accounts]);

    if (loading) {
        return <div>Loading reputation score...</div>;
    }

    if (error) {
        return <div>Error fetching reputation: {error}</div>;
    }

    return (
        <div>
            <h2>Reputation Score: {score !== null ? score : 'N/A'}</h2>
        </div>
    );
}

export default ReputationScore;
