import React, { useState, useContext } from 'react';
import './App.css';
import WalletConnectContext from './context/WalletConnectContext';
import ReputationScore from './components/ReputationScore';
import TransactionHistory from './components/TransactionHistory';
import GovernanceVoting from './components/GovernanceVoting';

function App() {
    const { walletKit } = useContext(WalletConnectContext);
    const [connectionError, setConnectionError] = useState(null);
    const [fetchedAccounts, setFetchedAccounts] = useState(); // Store fetched accounts

    const connectWallet = async () => {
        if (walletKit) {
            try {
                await walletKit.connect();
                const accounts = await walletKit.getAccounts();
                setFetchedAccounts(accounts); // Update fetched accounts
                setConnectionError(null);
                console.log('Connected accounts:', accounts);
            } catch (error) {
                console.error('Wallet connection error:', error);
                setConnectionError(error.message);
            }
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <button onClick={connectWallet}>Connect Wallet</button>
                {connectionError && <p style={{ color: 'red' }}>Connection Error: {connectionError}</p>}
                {fetchedAccounts && fetchedAccounts.length > 0 && ( // Use fetchedAccounts
                    <div>
                        <h3>Connected Accounts:</h3>
                        <ul>
                            {fetchedAccounts.map((account, index) => (
                                <li key={index}>{account}</li>
                            ))}
                        </ul>
                        <ReputationScore />
                        <TransactionHistory />
                        <GovernanceVoting />
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
