import { Core } from '@walletconnect/core';
import { WalletKit } from '@reown/walletkit';

const projectId = process.env.REACT_APP_PROJECT_ID; // Access your Project ID from environment variables

async function initializeWalletKit() {
    try {
        const core = new Core({
            projectId: projectId,
        });

        const walletKit = await WalletKit.init({
            core,
            metadata: {
                name: 'TrustChain DApp',
                description: 'DID and Reputation for DeFi',
                url: 'https://your-trustchain-app.com', // Replace with your app URL
                icons: ['https://your-app-icon.png'], // Replace with your app icon URL
                redirect: {
                    native: 'trustchain://', // Replace with your app's native redirect scheme if applicable
                },
            },
        });

        return walletKit;
    } catch (error) {
        console.error('WalletKit initialization error:', error);
        return null;
    }
}

export default initializeWalletKit;
